/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Move Forward
//
/////////////////////////////////////////////////////////////////////////////////////////

/**
 * Moves the bot forward. Enables motors {frontl, frontr, backl, backr} for __ seconds
 */
void forward(int seconds){
motor[frontl] = 100;
motor[backl] = 100;
motor[frontr] = 100;
motor[backr] = 100;
wait1Msec(seconds*1000);
motor[frontl] = 0;
motor[backl] = 0;
motor[frontr] = 0;
motor[backr] = 0;
}


/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Move Backward
//
/////////////////////////////////////////////////////////////////////////////////////////

/**
 * Moves the bot backward. Enables motors {frontl, frontr, backl, backr} for __ seconds
 */
void backward(int seconds){
motor[frontl] = -100;
motor[backl] = -100;
motor[frontr] = -100;
motor[backr] = -100;
wait1Msec(seconds*1000);
motor[frontl] = 0;
motor[backl] = 0;
motor[frontr] = 0;
motor[backr] = 0;
}


/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Rotate Right
//
/////////////////////////////////////////////////////////////////////////////////////////

/**
 * Rotates the bot to the right. Enables motors {frontl, frontr, backl, backr} for __ seconds
 */
void rotateRight(int seconds){
motor[frontr] = -100;
motor[backr] = -100;
motor[frontl] = 100;
motor[backl] = 100;
wait1Msec(seconds*1000);
}


/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Rotate Left
//
/////////////////////////////////////////////////////////////////////////////////////////

/**
 * Rotates the bot to the left. Enables motors {frontl, frontr, backl, backr} for __ seconds
 */
void rotateLeft(int seconds){
motor[frontr] = 100;
motor[backr] = 100;
motor[frontl] = -100;
motor[backl] = -100;
wait1Msec(seconds*1000);
}







