/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 User Control Task
//
/////////////////////////////////////////////////////////////////////////////////////////

/**
 * Period of match when driver controls the bot
 */
task usercontrol(){


  while (true)
	{
    if(bVEXNETActive){


      /////////////////////////////////////////////////////////////////////////////////////////
      //
      //                                      Drive
      //
      /////////////////////////////////////////////////////////////////////////////////////////
  		motor[BR] = vexRT[Ch2];
  		motor[FR] = vexRT[Ch2];
  		motor[BL] = vexRT[Ch3];
  		motor[FL] = vexRT[Ch3];

      wait1Msec(10);
    }
	}
}
